import * as React from "react";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import Link from "@mui/material/Link";
import InputAdornment from "@mui/material/InputAdornment";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import SendIcon from "@mui/icons-material/Send";

import { useSelector, useDispatch } from "react-redux";
import {
  fetchFootballers,
  selectFootballers,
} from "../features/counter/counterSlice";

export function QuestionCard() {
  const count = useSelector(selectFootballers);
  const dispatch = useDispatch();

  useEffect(() => {
    console.log("in Use effect");
    dispatch(fetchFootballers(1));
  }, []);

  return (
    <div>
      <Card sx={{ minWidth: 275, m: 5 }} variant="outlined">
        <CardContent>
          <Typography variant="h5" component="div">
            Identify the football player who has played with all these
            footballers :
          </Typography>
          <br />
          <Link href="https://en.wikipedia.org/wiki/Lionel_Messi">
            Lionel Messi
          </Link>
          <br />
          <br />
          <Link href="https://en.wikipedia.org/wiki/Cristiano_Ronaldo">
            Cristiano Ronaldo
          </Link>
          <br />
          <br />
          <Link href="https://en.wikipedia.org/wiki/Neymar">Neymar Jr</Link>
          <br />
          <br />
        </CardContent>
      </Card>
      <Box sx={{ flexGrow: 1, m: 5 }}>
        <TextField
          label="Your Answer here"
          id="outlined-start-adornment"
          sx={{ flexGrow: 1 }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">Footballer Name</InputAdornment>
            ),
          }}
        />
        <Button
          variant="contained"
          endIcon={<SendIcon />}
          sx={{ flexGrow: 1, ml: 5 }}
        >
          Submit
        </Button>
      </Box>
    </div>
  );
}
