import * as React from "react";
import { useState } from "react";

import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import ListSubheader from "@mui/material/ListSubheader";
import List from "@mui/material/List";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import SportsSoccerIcon from "@mui/icons-material/SportsSoccer";
import FlagIcon from "@mui/icons-material/Flag";
import LightbulbIcon from "@mui/icons-material/Lightbulb";
import IconButton from "@mui/material/IconButton";
import LockIcon from "@mui/icons-material/Lock";
import ListItem from "@mui/material/ListItem";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";
import Button from "@mui/material/Button";

import SendIcon from "@mui/icons-material/Send";

export function HintSection() {
  const onClick = (e) => {
    e.preventDefault();
    handleOpen();
  };

  const style = {
    position: "absolute",
    top: "20%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "2px solid #000",
    p: 4,
    borderRadius: 2,
  };

  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  return (
    <div>
      <Card sx={{ mt: 5, mr: 5 }} variant="outlined">
        <CardContent>
          <List
            sx={{ bgcolor: "background.paper" }}
            component="nav"
            aria-labelledby="nested-list-subheader"
            subheader={
              <ListSubheader component="div" id="nested-list-subheader">
                Hints
              </ListSubheader>
            }
          >
            <ListItem
              secondaryAction={
                <IconButton edge="end" aria-label="comments" onClick={onClick}>
                  <LockIcon />
                </IconButton>
              }
            >
              <ListItemIcon>
                <SportsSoccerIcon />
              </ListItemIcon>
              <ListItemText primary="Last Club of the player" />
            </ListItem>
            <ListItem
              secondaryAction={
                <IconButton edge="end" aria-label="comments" onClick={onClick}>
                  <LockIcon />
                </IconButton>
              }
            >
              <ListItemIcon>
                <FlagIcon />
              </ListItemIcon>
              <ListItemText primary="Country of the player" />
            </ListItem>
            <ListItem
              secondaryAction={
                <IconButton edge="end" aria-label="comments" onClick={onClick}>
                  <LockIcon />
                </IconButton>
              }
            >
              <ListItemIcon>
                <LightbulbIcon />
              </ListItemIcon>
              <ListItemText primary="Surprise Hint" />
            </ListItem>
          </List>
        </CardContent>
      </Card>

      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h6" component="h2">
            Do you really want to access the hint?
          </Typography>
          <Button
            variant="contained"
            type="submit"
            sx={{ flexGrow: 1, mt: 2, ml: 20 }}
            color="info"
          >
            No
          </Button>
          <Button
            variant="contained"
            type="submit"
            sx={{ flexGrow: 1, mt: 2, ml: 2, mr: 2 }}
            color="warning"
          >
            Access Hint
          </Button>
        </Box>
      </Modal>
    </div>
  );
}
