// React imports
import * as React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";

// MUI imports
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import InputAdornment from "@mui/material/InputAdornment";
import Link from "@mui/material/Link";
import SendIcon from "@mui/icons-material/Send";
import TextField from "@mui/material/TextField";
import Typography from "@mui/material/Typography";

// Code imports
import {
  fetchFootballers,
  selectFootballers,
} from "../features/counter/counterSlice";

const initialFValues = {
  footballerName: "",
};

export function QuestionCard() {
  const [values, setValues] = useState(initialFValues);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(values);
  };

  // define selector and instance of dispatch function
  const footballers = useSelector(selectFootballers);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchFootballers());
  }, [dispatch]);

  //console.log(footballers);

  return (
    <div>
      <Card sx={{ minWidth: 275, m: 5 }} variant="outlined">
        <CardContent>
          <Typography variant="h5" component="div">
            Identify the football player who has played with all these
            footballers :
          </Typography>
          <ul>
            {footballers.map((items, i) => {
              return (
                <div key={i}>
                  {items.map((item, j) => {
                    return (
                      <li key={j}>
                        {item.title + ",  " + item.body + " " + j}
                      </li>
                    );
                  })}
                </div>
              );
            })}
          </ul>
          <br />
          <Link href="https://en.wikipedia.org/wiki/Lionel_Messi">
            Lionel Messi
          </Link>
          <br />
          <br />
          <Link href="https://en.wikipedia.org/wiki/Cristiano_Ronaldo">
            Cristiano Ronaldo
          </Link>
          <br />
          <br />
          <Link href="https://en.wikipedia.org/wiki/Neymar">Neymar Jr</Link>
          <br />
          <br />
        </CardContent>
      </Card>
      <Box sx={{ flexGrow: 1, m: 5 }}>
        <form autoComplete="off" onSubmit={handleSubmit}>
          <TextField
            label="Your Answer here"
            id="outlined-start-adornment"
            sx={{ flexGrow: 1 }}
            name="footballerName"
            value={values.footballerName}
            onChange={handleInputChange}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  Footballer Name
                </InputAdornment>
              ),
            }}
          />
          <Button
            variant="contained"
            endIcon={<SendIcon />}
            sx={{ flexGrow: 1, ml: 5 }}
            type="submit"
          >
            Submit
          </Button>
        </form>
      </Box>
    </div>
  );
}
