// React imports
import * as React from "react";
import { useState } from "react";

// MUI imports
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Divider from "@mui/material/Divider";
import FlagIcon from "@mui/icons-material/Flag";
import IconButton from "@mui/material/IconButton";
import LightbulbIcon from "@mui/icons-material/Lightbulb";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import ListSubheader from "@mui/material/ListSubheader";
import LockIcon from "@mui/icons-material/Lock";
import LockOpenIcon from "@mui/icons-material/LockOpen";
import Modal from "@mui/material/Modal";
import SportsSoccerIcon from "@mui/icons-material/SportsSoccer";
import Typography from "@mui/material/Typography";

export function HintSection() {
  const onClick = (i) => {
    console.log(i);
    handleOpen();
    if (i > 2) return;

    setHints(
      hints.map((hint) => {
        if (hint.id === i + 1) {
          return { ...hint, disabled: false };
        }

        if (hint.id === i) {
          return { ...hint, locked: false };
        }

        // 👇️ otherwise return object as is
        return hint;
        return hint.id === i + 1
          ? Object.assign({}, hint, { disabled: false })
          : hint;
        return hint.id === i
          ? Object.assign({}, hint, { locked: false })
          : hint;
      })
    );
  };

  const style = {
    position: "absolute",
    top: "20%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "1px solid #000",
    p: 4,
    borderRadius: 2,
  };

  const initialHints = [
    {
      id: 0,
      text: "Last Club of the player",
      image: <SportsSoccerIcon />,
      disabled: false,
      locked: true,
    },
    {
      id: 1,
      text: "Country of the player",
      image: <FlagIcon />,
      disabled: true,
      locked: true,
    },
    {
      id: 2,
      text: "Surprise Hint",
      image: <LightbulbIcon />,
      disabled: true,
      locked: true,
    },
  ];

  const [hints, setHints] = useState(initialHints);
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  return (
    <div>
      <Card sx={{ mt: 5, mr: 5 }} variant="outlined">
        <CardContent>
          <List
            sx={{ bgcolor: "background.paper" }}
            component="nav"
            subheader={<ListSubheader component="div">Hints</ListSubheader>}
          >
            {hints.map((hint, i) => {
              return (
                <ListItem
                  secondaryAction={
                    <IconButton
                      edge="end"
                      onClick={() => onClick(hint.id)}
                      disabled={hint.disabled}
                    >
                      {hint.locked ? <LockIcon /> : <LockOpenIcon />}
                    </IconButton>
                  }
                  key={hint.id}
                >
                  <ListItemIcon>{hint.image}</ListItemIcon>
                  <ListItemText primary={hint.text} />
                </ListItem>
              );
            })}
          </List>
        </CardContent>
      </Card>

      <Modal
        open={open}
        onClose={(_, reason) => {
          if (reason !== "backdropClick") {
            handleClose();
          }
        }}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography
            id="modal-modal-title"
            variant="h6"
            component="h2"
            sx={{ textAlign: "center" }}
          >
            Do you really want to access the hint?
          </Typography>
          <Divider sx={{ textAlign: "center" }} />
          <Button
            variant="contained"
            sx={{ flexGrow: 1, mt: 2, ml: 20 }}
            color="info"
            onClick={() => {
              handleClose();
            }}
          >
            No
          </Button>
          <Button
            variant="contained"
            sx={{ flexGrow: 1, mt: 2, ml: 2, mr: 2 }}
            color="warning"
          >
            Access Hint
          </Button>
        </Box>
      </Modal>
    </div>
  );
}
