import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const initialState = {
  loading: true,
  footballers: [],
};

export const fetchHints = createAsyncThunk("hints/fetchHints", async () => {
  try {
    const apiUrl = "http://127.0.0.1:5000/api/v1/footballers/";

    var config = {
      method: "get",
      url: apiUrl,
      headers: {
        accept: "application/json",
        "Access-Control-Allow-Origin": "*",
      },
    };

    const response = await axios(config);
    const data = await response.data;
    //console.log(data);
    return data;
  } catch (error) {
    console.log(error);
  }
});

export const hintsSlice = createSlice({
  name: "hints",
  initialState,
  // The `extraReducers` field lets the slice handle actions defined elsewhere,
  // including actions generated by createAsyncThunk or in other slices.
  extraReducers: (builder) => {
    builder
      .addCase(fetchHints.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchHints.fulfilled, (state, action) => {
        state.loading = false;
        state.footballers = [];
        state.footballers.push(action.payload);
      });
  },
});

export const selectFootballers = (state) => state.footballers.footballers;

export default hintsSlice.reducer;
